    public void reestablecerPass(View view){
        final String emailAddress = edittextEmailIngresar.getText().toString();
        boolean isValid = verifyEmail(emailAddress);

        if(isValid) {
            mAuth.sendPasswordResetEmail(emailAddress)
                    .addOnCompleteListener(new OnCompleteListener<Void>() {
                        @Override
                        public void onComplete(@NonNull Task<Void> task) {
                            if (task.isSuccessful()) {
                                Log.d("REESTABLSHING", "Email sent.");
                                Toast.makeText(getBaseContext(), "Un email fue enviado a "+emailAddress+" para reestablecer la contraseña. Verifique su correo no desea (spam)", Toast.LENGTH_LONG).show();
                            }
                        }
                    });
        }else{
            edittextEmailIngresar.setError("Debe escribir un correo válido para reestablecer su contraseña");
        }
    }

    public void showMain(View view, Provider provider, String email, String username, String image_url, String uid) {
        Intent intent = new Intent(this, CategoriasActivity.class);

        intent.putExtra(KEY_PROVIDER, provider.name());
        intent.putExtra(KEY_EMAIL, email);
        intent.putExtra(KEY_USERNAME, username);
        intent.putExtra(KEY_IMAGE_URL, image_url);
        intent.putExtra(KEY_UID, uid);

        startActivity(intent);
    }

    public void openMain(View view){
        showMain(view, Provider.GUEST, "", "", "", "");
    }

    public void loginEmail(View view){
        if(verifyEmail(edittextEmailIngresar.getText().toString()) && !edittextContrasenaIngresar.getText().toString().isEmpty()){
            String email = edittextEmailIngresar.getText().toString();
            String pass = edittextContrasenaIngresar.getText().toString();

            mAuth.signInWithEmailAndPassword(email, pass).addOnCompleteListener(new OnCompleteListener<AuthResult>() {
                @Override
                public void onComplete(@NonNull Task<AuthResult> task) {
                    if(task.isSuccessful()) {
                        Toast.makeText(getBaseContext(), "Bienvenido, " + task.getResult().getUser().getEmail(), Toast.LENGTH_SHORT).show();
                        showMain(
                                null,
                                Provider.EMAIL,
                                task.getResult().getUser().getEmail(),
                                task.getResult().getUser().getEmail(),
                                "",
                                mAuth.getCurrentUser().getUid());
                    }
                    else Toast.makeText(getBaseContext(), getString(R.string.error_login_email), Toast.LENGTH_SHORT).show();
                }
            });
        }else{
            if(!verifyEmail(edittextEmailIngresar.getText().toString())) edittextEmailIngresar.setError("Debe escribir un email válido");
            if(edittextContrasenaIngresar.getText().toString().isEmpty()) edittextContrasenaIngresar.setError("Debe escribrir una contraseña");
        }
    }

    public void loginGoogle(View view){
        GoogleSignInOptions googleConf = new GoogleSignInOptions.Builder(GoogleSignInOptions.DEFAULT_SIGN_IN)
                .requestIdToken(getString(R.string.default_web_client_id))
                .requestEmail()
                .build();

        GoogleSignInClient googleClient = GoogleSignIn.getClient(this, googleConf);

        googleClient.signOut(); //cierra sesión para que siempre tenga que autenticarse
        startActivityForResult(googleClient.getSignInIntent(), GOOGLE_LOGIN);
    }

    public void loginFacebook(View view){
        LoginManager.getInstance().logOut();
        LoginManager.getInstance().logInWithReadPermissions(this, Arrays.asList("email", "public_profile"));
        LoginManager.getInstance().registerCallback(callbackManager, new FacebookCallback<LoginResult>() {
            @Override
            public void onSuccess(LoginResult loginResult) {
                AccessToken token = loginResult.getAccessToken();
                AuthCredential credential = FacebookAuthProvider.getCredential(token.getToken());
                mAuth.signInWithCredential(credential)
                        .addOnCompleteListener(new OnCompleteListener<AuthResult>() {
                            @Override
                            public void onComplete(@NonNull Task<AuthResult> task) {
                                if (task.isSuccessful()){
                                    Toast.makeText(getBaseContext(), "Bienvenido, "+mAuth.getCurrentUser().getDisplayName(), Toast.LENGTH_SHORT).show();
                                    showMain(
                                            null,
                                            Provider.FACEBOOK,
                                            mAuth.getCurrentUser().getEmail(),
                                            mAuth.getCurrentUser().getDisplayName(),
                                            getImageUrl(mAuth.getCurrentUser()),
                                            mAuth.getCurrentUser().getUid());
                                }
                                else
                                    Toast.makeText(getBaseContext(), "Error: "+task.getException().getMessage(), Toast.LENGTH_SHORT).show();
                            }
                        });
            }

            @Override
            public void onCancel() {

            }

            @Override
            public void onError(FacebookException error) {
                Toast.makeText(getBaseContext(), "Error: "+error.getMessage(), Toast.LENGTH_SHORT).show();
            }
        });
    }

    private String getImageUrl(FirebaseUser user){
        String image_http = "https://";
        String image_host = user.getPhotoUrl().getHost();
        String image_path = user.getPhotoUrl().getPath();
        return image_http + image_host + image_path;
    }
