    public void signupEmail(View view){
        if(!edittextEmailRegistrar.getText().toString().isEmpty() && !edittextContrasenaRegistrar.getText().toString().isEmpty()){
            String email = edittextEmailRegistrar.getText().toString();
            String pass = edittextContrasenaRegistrar.getText().toString();

            if(verifyEmail(email) && pass.length() >= 6) {
                mAuth.createUserWithEmailAndPassword(email, pass).addOnCompleteListener(new OnCompleteListener<AuthResult>() {
                    @Override
                    public void onComplete(@NonNull Task<AuthResult> task) {
                        if (task.isSuccessful()) {
                            System.out.println("SUCCESS");
                            Toast.makeText(getBaseContext(), getString(R.string.success_registrar_email), Toast.LENGTH_SHORT).show();

                            FirebaseUser user = mAuth.getCurrentUser();
                            showMain(
                                    null,
                                    Provider.EMAIL,
                                    user.getEmail(),
                                    user.getEmail(),
                                    "",
                                    user.getUid()
                            );
                        } else {
                            if(task.getException().getLocalizedMessage().equals("The email address is already in use by another account.")){
                                Toast.makeText(getBaseContext(), getString(R.string.error__email_usado), Toast.LENGTH_SHORT).show();
                            }
                        }
                    }
                });
            }else{
                if(!verifyEmail(email)) edittextEmailRegistrar.setError("Email inválido");
                if(pass.length() < 6) edittextContrasenaRegistrar.setError("La contraseña debe tener al menos 6 caracteres");
            }
        }
    }

    public void showMain(View view, Provider provider, String email, String username, String image_url, String uid) {
        Intent intent = new Intent(this, CategoriasActivity.class);

        intent.putExtra(KEY_PROVIDER, provider.name());
        intent.putExtra(KEY_EMAIL, email);
        intent.putExtra(KEY_USERNAME, username);
        intent.putExtra(KEY_IMAGE_URL, image_url);
        intent.putExtra(KEY_UID, uid);

        startActivity(intent);
    }

    public void openIngresar(View view){
        finish();
    }

    private void openMain(){
        Intent intent = new Intent(getBaseContext(), MainActivity.class);
        startActivity(intent);
    }
